import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';


const ShowUsers = () => {

    const endpoint = 'http://127.0.0.1:8000/api';

    const [users, setUsers] = useState( [] );

    useEffect( () => {
        getAllUsers()
    }, []);

    const getAllUsers = async () => {
        const users = await fetch(endpoint + "/Usuarios");
        const json = await users.json();
        setUsers(json);
    }

    const deleteUser = async (id) => {
        fetch(endpoint + "/Usuario/"+id, {
            method: 'DELETE',
        }).then(response => response.json());
    }

    return (

        <div className="card" >
            <div className="card-body">
                <h5 className="card-title">Lista de Usuarios</h5>
                <div className="d-flex justify-content-center">
                    <Link to="/create" className="btn btn-label-primary "> Registrar nuevo usuario </Link>
                </div>
                
                <div className="table-responsive">
                    <table className="table">
                        <thead className="table">
                            <tr>
                                <th> Nombre completo</th>
                                <th>Email</th>
                                <th>Dirección</th>
                                <th>Teléfono</th>
                                <th>Fecha de nacimiento</th>
                                <th>Edad</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            { users.map(  (user) => ( 
                                <tr key={user.ID} >
                                    <td> { user.Name } </td>
                                    <td> { user.Email } </td>
                                    <td> { user.Direccion } </td>
                                    <td> { user.Telefono } </td>
                                    <td> { user.FechaNacimiento } </td>
                                    <td> { user.Edad } </td>
                                    <td className="d-flex">
                                        <Link to={`/edit/${ user.ID }`} className="btn rounded-pill me-2 btn-label-warning"> Editar </Link>
                                        <button onClick={ ()=>deleteUser(user.ID)  } type="button" className="btn rounded-pill me-2 btn-label-danger"> Eliminar </button>
                                    </td>
                                    
                                </tr>
                            ))}
                            
                        </tbody>
                    </table>
                </div>

            </div>
        </div>

    );
}


export default ShowUsers;
