import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';


const ShowUsers = () => {

    const endpoint = 'http://127.0.0.1:8000/api';

    const [users, setUsers] = useState([]);

    useEffect(() => {
        getAllUsers();
    }, []);

    const getAllUsers = async () => {
        const users = await fetch(endpoint + "/Usuarios");

        const json = await users.json();

        setUsers(json);
        
    }

    return (

        <div class="card" >
            <div class="card-body">
                <h5 class="card-title">Lista de Usuarios</h5>
                <div class="table-responsive">
                    <table class="table">
                        <thead class="table">
                            <tr>
                                <th>Nombre completo</th>
                                <th>Email</th>
                                <th>Dirección</th>
                                <th>Teléfono</th>
                                <th>Fecha de nacimiento</th>
                                <th>Edad</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            { users.map(  (user) => ( 
                                <tr>
                                    <td> { user.Name } </td>
                                    <td> { user.Email } </td>
                                    <td> { user.Direccion } </td>
                                    <td> { user.Telefono } </td>
                                    <td> { user.FechaNacimiento } </td>
                                    <td> { user.FechaNacimiento } </td>
                                    <td>
                                        <button type="button" class="btn rounded-pill me-2 btn-label-primary"> Editar </button>
                                        <button type="button" class="btn rounded-pill me-2 btn-label-primary"> Eliminar </button>
                                    </td>
                                    
                                </tr>
                            ))}
                            
                        </tbody>
                    </table>
                </div>

            </div>
        </div>

    );
}


export default ShowUsers;
